    private void searchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_searchActionPerformed
        
        matricula = inputMatricula.getText();
        
        Registros registros = new Registros();
        
        try {
            // Conectar a la base de datos
            registros.conectar("jdbc:mysql://localhost:3306/registro_asistencia_eventos", "root", "");

            // Ejecutar una consulta y mostrar resultados
            ResultSet resultado = registros.ejecutarConsulta("SELECT * FROM alumnos WHERE matricula = '" + matricula + "';");
            registro = registros.mostrarRegistro(resultado, "matricula", "nombre", "apellido_p", "apellido_m", "semestre");
            System.out.println(registro);
            /*
            // Agregar un nuevo registro
            registros.agregarRegistro("INSERT INTO usuarios (nombre, edad) VALUES ('Ejemplo', 30)");

            // Editar un registro
            registros.editarRegistro("UPDATE usuarios SET edad = 31 WHERE nombre = 'Ejemplo'");

            // Eliminar un registro
            registros.eliminarRegistro("DELETE FROM usuarios WHERE nombre = 'Ejemplo'");
            */

        } catch (SQLException e) {
            e.printStackTrace();
        } finally {
            try {
                // Desconectar de la base de datos
                registros.desconectar();
            } catch (SQLException e) {
                e.printStackTrace();
            }
        }
        
        output.setText(registro);
        System.out.println(registro);
    }//GEN-LAST:event_searchActionPerformed

